    public int totalAttempts = 0;
    
    // Section of code for the first question
    private void submitQ1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitQ1ActionPerformed
        String answer = firstAnswer.getText();
        firstAnswer.setEditable( false );
        firstAnswer.setEnabled( false );
        submitQ1.setEnabled( false );
        
        if (answer.equals ("C") || answer.equals ("c")) {
            result1.setText("Correct");
            correct = correct + 1;
        } 
        else
        {
            result1.setText("Incorrect");
            incorrect = incorrect + 1;
        }
        correctAnswers.setText("Correct answers: " + correct );
        incorrectAnswers.setText( "Incorrect answers: " + incorrect);
        percentCorrect.setText( String.format( "Percent Correct: %2.1f%%", 100.0 * correct / 5.0 ));
      

    }//GEN-LAST:event_submitQ1ActionPerformed
